mapper.select:
select 

from offitem
where OFFITEM_ID = #{offitemId,jdbcType=INTEGER}
  and ITEM_ID = #{itemId,jdbcType=INTEGER}

mapper.delete:
delete from offitem
where OFFITEM_ID = #{offitemId,jdbcType=INTEGER}
  and ITEM_ID = #{itemId,jdbcType=INTEGER}

mapper.update:
update offitem


    ADMIN_ID = #{adminId,jdbcType=INTEGER},


    OFFITEM_DATE = #{offitemDate,jdbcType=DATE},


    OFFITEM_TIME = #{offitemTime,jdbcType=INTEGER},


where OFFITEM_ID = #{offitemId,jdbcType=INTEGER}
  and ITEM_ID = #{itemId,jdbcType=INTEGER}

update offitem
set ADMIN_ID = #{adminId,jdbcType=INTEGER},
  OFFITEM_DATE = #{offitemDate,jdbcType=DATE},
  OFFITEM_TIME = #{offitemTime,jdbcType=INTEGER}
where OFFITEM_ID = #{offitemId,jdbcType=INTEGER}
  and ITEM_ID = #{itemId,jdbcType=INTEGER}
