mapper.select:
select 

from offshop
where OFFSHOP_ID = #{offshopId,jdbcType=INTEGER}
  and SHOP_ID = #{shopId,jdbcType=INTEGER}

mapper.delete:
delete from offshop
where OFFSHOP_ID = #{offshopId,jdbcType=INTEGER}
  and SHOP_ID = #{shopId,jdbcType=INTEGER}

mapper.update:
update offshop


    ADMIN_ID = #{adminId,jdbcType=INTEGER},


    OFFSHOP_DATE = #{offshopDate,jdbcType=DATE},


    OFFSHOP_TIME = #{offshopTime,jdbcType=INTEGER},


where OFFSHOP_ID = #{offshopId,jdbcType=INTEGER}
  and SHOP_ID = #{shopId,jdbcType=INTEGER}

update offshop
set ADMIN_ID = #{adminId,jdbcType=INTEGER},
  OFFSHOP_DATE = #{offshopDate,jdbcType=DATE},
  OFFSHOP_TIME = #{offshopTime,jdbcType=INTEGER}
where OFFSHOP_ID = #{offshopId,jdbcType=INTEGER}
  and SHOP_ID = #{shopId,jdbcType=INTEGER}
