mapper.select:
select 

from offuser
where OFFUSER_ID = #{offuserId,jdbcType=INTEGER}
  and USER_ID = #{userId,jdbcType=INTEGER}

mapper.delete:
delete from offuser
where OFFUSER_ID = #{offuserId,jdbcType=INTEGER}
  and USER_ID = #{userId,jdbcType=INTEGER}

mapper.update:
update offuser


    ADMIN_ID = #{adminId,jdbcType=INTEGER},


    OFFUSER_DATE = #{offuserDate,jdbcType=DATE},


    OFFUSER_TIME = #{offuserTime,jdbcType=INTEGER},


where OFFUSER_ID = #{offuserId,jdbcType=INTEGER}
  and USER_ID = #{userId,jdbcType=INTEGER}

update offuser
set ADMIN_ID = #{adminId,jdbcType=INTEGER},
  OFFUSER_DATE = #{offuserDate,jdbcType=DATE},
  OFFUSER_TIME = #{offuserTime,jdbcType=INTEGER}
where OFFUSER_ID = #{offuserId,jdbcType=INTEGER}
  and USER_ID = #{userId,jdbcType=INTEGER}
