mapper.select:
select 

from shop
where SHOP_ID = #{shopId,jdbcType=INTEGER}
  and SHOP_NAME = #{shopName,jdbcType=VARCHAR}

mapper.delete:
delete from shop
where SHOP_ID = #{shopId,jdbcType=INTEGER}
  and SHOP_NAME = #{shopName,jdbcType=VARCHAR}

mapper.insert:
insert into shop (SHOP_ID, SHOP_NAME, PASSWORD, 
  EMAIL, PHONE, OWNER_NAME
  )
values (#{shopId,jdbcType=INTEGER}, #{shopName,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, 
  #{email,jdbcType=VARCHAR}, #{phone,jdbcType=VARCHAR}, #{ownerName,jdbcType=VARCHAR}
  )

mapper.update:
update shop
set PASSWORD = #{password,jdbcType=VARCHAR},
  EMAIL = #{email,jdbcType=VARCHAR},
  PHONE = #{phone,jdbcType=VARCHAR},
  OWNER_NAME = #{ownerName,jdbcType=VARCHAR}
where SHOP_ID = #{shopId,jdbcType=INTEGER}
  and SHOP_NAME = #{shopName,jdbcType=VARCHAR}
